package language.basics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//1.Temperature of a city in degrees Celsius: 25.5
		float temparature = 25.5f;
		System.out.println("Temperature of city: "+temparature);
		
		//2. Whether a customer has placed an order: true or false
		boolean orderplaced = true;
		System.out.println("order status: "+orderplaced);
		
		//3.Person's phone number: "123-456-7890"
		String Phonenumber = "123-456-7890";
		System.out.println("person phone number: "+Phonenumber);
		
		//4.Amount of money in a customer's bank account: 1000.50
		float bankbalance = 1000.50f;
		System.out.println("account balance: "+bankbalance);
		
		//5.Person's email address: "john.doe@example.com"
		String email = "john.doe@example.com";
		System.out.println("person's email: "+email);
		
		//6.Coordinates of a location (latitude, longitude): 37.7749, -122.4194
		double latitude = 37.7749;
		double longitude = -122.4194;
		System.out.println("Coordinates: (" + latitude + ", " + longitude + ")");
		
		//7.Person's marital status: true or false
		boolean maritalstatus = true;
		System.out.println("person is married: "+maritalstatus);
		
		//8.Person's occupation: "Software Engineer"
		String occupation = "Software Engineer";
		System.out.println("person's occupation: "+occupation);
		
		//9.Person's favourite colour: "Blue"
		String colour = "blue";
		System.out.println("person's favourite colour: "+colour);
		
		//10.Current year: 2023
		int year = 2023;
		System.out.println("Current Year: " +year);
		
		//11.Number of followers on a social media platform: 1,000,000
		long followers = 1_000_000L;
		System.out.println("Followers: " + followers);
		
		//12.Rating of a movie: 7.5
		float rating = 7.5f;
		System.out.println("Movie Rating: " +rating);
		
		//13.Person's blood type: 'A'
		char type = 'A';
		System.out.println("person's Blood Type: "+type);
		
		//14.Title of a book: "To Kill a Mockingbird"
		String bookTitle = "To Kill a Mockingbird";
		System.out.println("Title Of Book: "+bookTitle);
		
		//15.Number of employees in a company: 500
		int totalEmployees = 500;
		System.out.println("Total Number Of Employees In Company: "+totalEmployees);
		
		//16.Time of an event: 2:30 PM
		String time = "02:30 PM";
		System.out.println("Event Time: "+time);
		
		//17.Name of a country: "United States"
		String country = "United States";
		System.out.println("Name Of The Country: "+country);
		
		//18.Person's eye color: "Brown"
		String color = "Brown";
		System.out.println("Eye Color: "+color);
		
		//19.Person's birthplace: "New York City"
		String birthPlace = "New York City";
		System.out.println("Place Of Birth: "+birthPlace);
		
		//20.Distance between two cities: 200.5
		double distance = 200.5;
		System.out.println("Distance Between Two Cities In KM: "+distance);
		
		
				
		
		
		
				
		
			
		
	}

}
